#include <iostream>
using namespace std;struct P{double x,y,z;};P cp(P a,P b){P p;p.x=a.y*b.z-a.z*b.y;p.y=a.z*b.x-a.x*b.z;p.z=a.x*b.y-a.y*b.x;return p;}P orth(P a){P b;b.x=-a.y;b.y=a.x;b.z=a.z;return b;}int main(){cout.precision(9);int t;cin>>t;for(int ti=1;ti<=t;++ti){double x,y;P a,b,c;cin>>x>>y;a=P{x,y,1};cin>>x>>y;b=P{x,y,1};cin>>x>>y;c=P{x,y,1};P lAB,lAC,lBC;lAB=cp(a,b);lAC=cp(a,c);lBC=cp(b,c);cout<<"Case #"<<ti<<":\n"<<(a.x+b.x+c.x)/3<<" "<<(a.y+b.y+c.y)/3<<"\n";P ab,ac;ab=cp(cp(lAB,cp(c,orth(cp(lAB,P{0,0,1})))),c);ac=cp(cp(lAC,cp(b,orth(cp(lAC,P{0,0,1})))),b);P i=cp(ab,ac);i.x/=i.z;i.y/=i.z;cout<<i.x<<" "<<i.y<<"\n";P mAB,mAC;mAB=P{(a.x+b.x)/2,(a.y+b.y)/2,1};mAC=P{(a.x+c.x)/2,(a.y+c.y)/2,1};P bAB,bAC;P oAB=P{-(b.y-a.y)+mAB.x,b.x-a.x+mAB.y,1};P oAC=P{-(c.y-a.y)+mAC.x,c.x-a.x+mAC.y,1};bAB=cp(mAB,oAB);bAC=cp(mAC,oAC);i=cp(bAB,bAC);i.x/=i.z;i.y/=i.z;cout<<i.x<<" "<<i.y<<"\n";}return 0;}